INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (283, 'স্যার ওয়াল্টার স্কট' , 'https://www.rokomari.com/static/new/img/author/author.png', null);
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (358, 'মুস্তাফা জামান আব্বাসী' , 'https://ds.rokomari.store/rokomari110/people/ade96eac2_10.jpg', 'মুস্তাফা জামান আব্বাসী (জন্ম: ৮ ডিসেম্বর, ১৯৩৬) একজন বাংলাদেশি সঙ্গীতজ্ঞ এবং সঙ্গীত বিষয়ক অধ্যাপক ও গবেষক। তিনি লোক সঙ্গীতশিল্পী ও সুরকার আব্বাসউদ্দীন আহমদের কনিষ্ঠপুত্র। সঙ্গীতে অবদানের জন্য তিনি ১৯৯৫ সালে বাংলাদেশ সরকার প্রদত্ত সর্বোচ্চ বেসামরিক পুরস্কার একুশে পদকে ভূষিত হন এবং লেখালেখির স্বীকৃতি স্বরূপ পেয়েছেন বাংলা একাডেমি ফেলোশিপ। তিনি ইন্ডিপেন্ডেন্ট ইউনিভার্সিটির "কাজী নজরুল ইসলাম এবং আব্বাসউদ্দীন আহমদ গবেষণা ও শিক্ষা কেন্দ্র" এর গবেষক। নজরুল ইসলামকে নিয়ে তার কাজের জন্য তিনি ২০১৩ সালে নজরুল মেলায় আজীবন সম্মাননা লাভ করেন। তিনি তৎকালীন ব্রিটিশ ভারতের পশ্চিমবঙ্গের কুচবিহার জেলায় জন্মগ্রহণ করেন। তার বড় ভাই মোস্তফা কামাল ছিলেন বাংলাদেশের প্রধান বিচারপতি। তার বোন ফেরদৌসী রহমান একজন সুরকার ও সঙ্গীতশিল্পী। আব্বাসী ভারতীয় শাস্ত্রীয় সঙ্গীতজ্ঞ ওস্তাদ মুহম্মদ হোসেন খসরু এবং ওস্তাদ গুল মোহাম্মদ খাঁর নিকট সঙ্গীতের তালিম গ্রহণ করেন। তিনি মোট ৫০টি বই রচনা/সম্পাদনা করেন। তিনি ভাওয়াইয়া গানের উপর দুটি বই প্রকাশ করেন যার মধ্যে ১২০০ গানের উল্লেখ রয়েছে। তিনি আমার ঠিকানা এবং ভরা নদীর বাঁকে টেলিভিশন অনুষ্ঠানের উপস্থাপনা করেছেন। অনুষ্ঠান দুটি বাংলাদেশ টেলিভিশনে প্রচারিত হত।');
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (371, 'হাফিজ আল ফারুকী' , 'https://www.rokomari.com/static/new/img/author/author.png', null);
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (375, 'আল মাহমুদ' , 'https://ds.rokomari.store/rokomari110/people/b623143d88a4_63.jpg', 'বাংলা সাহিত্যের শ্রেষ্ঠ কবিদের একজন আল মাহমুদের কবিতার বই পড়েননি এমন সাহিত্যপ্রেমী খুঁজে পাওয়া ভার। গুণী এই কবি একাধারে একজন সাংবাদিক, শিশুসাহিত্যিক, ঔপন্যাসিক ও প্রাবন্ধিক। তবে সবকিছু ছাপিয়ে গেছে তার কবি পরিচয়। আধুনিক বাংলা কবিতা নানা দিক থেকে তার কাছে ঋণী থাকবে। বাচনভঙ্গি আর রচনাশৈলীতে তার কবিতা সমকালীন যেকোনো কবির তুলনায় অনন্য। ‘কবিতাসমগ্র’ (দুই খন্ড) ‘উড়ালকাব্য’, ‘সোনালি কাবিন’, ‘আল মাহমুদের স্বাধীনতার কবিতা’, ‘প্রেমের কবিতা সমগ্র’, ‘আল মাহমুদের শ্রেষ্ঠ কবিতা’ ইত্যাদি কবিতার বই নিয়ে আল মাহমুদ কবিতাসমগ্র। এছাড়াও আল মাহমুদ উপন্যাস সমগ্র প্রকাশিত হয়েছে তিন খণ্ডে');
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (389, 'খন্দকার মাহমুদুল হাসান' , 'https://www.rokomari.com/static/new/img/author/author.png', 'Khandokar Mahmudul Hasan');
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (408, 'বিপ্রদাশ বড়ুয়া' , 'https://www.rokomari.com/static/new/img/author/author.png', 'Bipradash Barua- জন্ম ২ আশ্বিন ১৮৬২ শকাব্দ (২০ সেপ্টেম্বর ১৯৪০) চট্টগ্রামের ইছামতী গ্রামে। পড়াশুনা চট্টগ্রাম, রাঙামাটি ও ঢাকা বিশ্ববিদ্যালয়ে। গল্প, উপন্যাস, প্রকৃতি ও বিচিত্র বিষয়ে লেখেন। পাখি, সমুদ্র ও বৃক্ষ, বনমর্মরভূমি, ভ্রমণ, গবেষণা-বিশাল লেখার ভুবন। বাংলাদেশের আনাচ-কানাচ উঠে আসে দৈনিক পত্রিকার কলামে।');
INSERT INTO AUTHOR (AUTHOR_ID, NAME, IMAGE, DESCRIPTION) VALUES (410, 'আনিসুজ্জামান' , 'https://ds.rokomari.store/rokomari110/people/1fa95e2eca94_1009.jpg', 'আনিসুজ্জামান একাধারে একজন বরেণ্য শিক্ষাবিদ, লেখক, গবেষক, ভাষা সংগ্রামী, সংবিধানের অনুবাদক এবং ইমেরিটাস অধ্যাপক। এককথায় তিনি একজন জীবন্ত কিংবদন্তি। এই গুণীজন ১৯৩৭ সালের ১৮ই ফেব্রুয়ারি ভারতের পশ্চিমবঙ্গের চব্বিশ পরগনা জেলার বসিরহাটে এক উচ্চশিক্ষিত পরিবারে জন্মগ্রহণ করেন। তার বর্ণাঢ্য শিক্ষাজীবন শুরু হয় কলকাতার পার্ক সার্কাস হাই স্কুল থেকে। কৈশোরে পরিবারসমেত বাংলাদেশে চলে আসলে খুলনা শহরের এক স্কুলে ভর্তি হন। ১৯৫১ সালে ঢাকার প্রিয়নাথ হাই স্কুল থেকে মাধ্যমিক এবং ১৯৫৩ সালে জগন্নাথ কলেজ থেকে উচ্চ মাধ্যমিক পাস করেন আনিসুজ্জামান। এরপর তিনি ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে ভর্তি হন। সেখানে প্রথম শ্রেণীতে প্রথম স্থান অর্জন করে স্নাতকোত্তর সম্পন্ন করেন।');
